class MemoryCache {
  constructor() {
    this.cache = void 0;
    this.cache = new Map();
  }
  get(key, ttl) {
    const item = this.cache.get(key);
    if (!item) return null;
    const now = Date.now();
    if (now - item.timestamp > ttl) {
      this.cache.delete(key);
      return null;
    }
    return item.value;
  }
  set(key, value) {
    this.cache.set(key, {
      value,
      timestamp: Date.now()
    });
  }
  clear() {
    this.cache.clear();
  }
}
